'use strict';

let sha1 = require('bem-sha1');
let md5 = require('bem-md5');
let crypto = require('crypto');

let base64 = {
  encode: function(str) {
    return new Buffer(str).toString('base64');
  },
  decode: function(str) {
    return new Buffer(str, 'base64').toString()
  }
};

let BemCrypto = {
  sha1, md5, base64
};

module.exports = BemCrypto;
