'use strict'

var assert = require("assert");
var md5 = require('./');

describe('md5', function() {
  it('加密123，不带key', function(done) {
    var encrypt = md5('123');
    assert(encrypt === '202cb962ac59075b964b07152d234b70');
    done();
  });

  it('加密123，带key', function(done) {
    var encrypt = md5('123', 'key');
    assert.equal(encrypt, '52851cb05258c8d98da1672d95729e53');
    done();
  });
});
