'use strict'

var assert = require("assert");
var BemSleep = require('./');
let co = require('co');

describe('BemSleep', function() {
  describe('set', function() {
    it('it', function(done) {
      co(function*() {
        let t1 = Date.now();
        let timer = yield BemSleep(1000);
        let t2 = Date.now();
        assert(t2 - t1 >= 1000);
        assert(timer);
      }).then(done, done);
    });
  });
});
